    AdView vBannerAdViewAmusingFactListActivity;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_amusing_fact_list);

        vPreviousMenuAmusingFactListActivity = findViewById(R.id.previousMenuImageViewAmusingFactListActivity);
        vPreviousMenuAmusingFactListActivity.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Intent angerIntentAmusingFactListActivity = new Intent(AmusingFactList.this, Category.class);
                startActivity(angerIntentAmusingFactListActivity);
                MusicComponent.shouldPlayCategoryActivity = false;
                MusicComponent.shouldPlayAmusingFactListActivity = true;

            }
        });

        for(i = 0; i <= 998; i++){
            vFactNumbersAmusingFactListActivity[i] = "Fact #" + (1 + i);
        }

        for (j = 0; j <= 998; j++) {
            extraTextAmusingFactListActivity[j] = "EXTRA_TEXT_AMUSING_FACT_LIST_ACTIVITY" + j;
        }

        vFactNumberListViewAmusingFactListActivity = findViewById(R.id.factNumberListViewAmusingFactListActivity);

        AmusingFactsAdapter amusingFactsAdapterAmusingFactListActivity = new AmusingFactsAdapter();
        vFactNumberListViewAmusingFactListActivity.setAdapter(amusingFactsAdapterAmusingFactListActivity);


        vFactNumberListViewAmusingFactListActivity.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                Toast.makeText(AmusingFactList.this,vFactNumbersAmusingFactListActivity[position],Toast.LENGTH_SHORT).show();
                vFactNumberAmusingFactListActivity = position;

                Intent amusingFactIntentAmusingFactListActivity = new Intent(AmusingFactList.this, AmusingFact.class);

                for (k = 0; k <= 998; k++) {
                    amusingFactIntentAmusingFactListActivity.putExtra(extraTextAmusingFactListActivity[k], amusingFacts[k]);
                }

                amusingFactIntentAmusingFactListActivity.putExtra(EXTRA_NUMBER_AMUSING_FACT_LIST_ACTIVITY, vFactNumberAmusingFactListActivity);
                startActivity(amusingFactIntentAmusingFactListActivity);
                MusicComponent.shouldPlayAmusingFactListActivity = true;
                MusicComponent.shouldPlayAmusingFactActivity = false;

            }
        });

        MobileAds.initialize(this, new OnInitializationCompleteListener() {
            @Override
            public void onInitializationComplete(InitializationStatus initializationStatus) {
            }
        });
        vBannerAdViewAmusingFactListActivity = findViewById(R.id.bannerAdViewAmusingFactListActivity);
        AdRequest vBannerAdRequestAmusingFactListActivity = new AdRequest.Builder().build();
        vBannerAdViewAmusingFactListActivity.loadAd(vBannerAdRequestAmusingFactListActivity);



    }



    class AmusingFactsAdapter extends BaseAdapter {

        @Override
        public int getCount() {
            return vFactNumbersAmusingFactListActivity.length;
        }

        @Override
        public Object getItem(int position) {
            return null;
        }

        @Override
        public long getItemId(int position) {
            return 0;
        }

        @Override
        public View getView(int position, View view, ViewGroup parent) {

            view = View.inflate(AmusingFactList.this, R.layout.amusing_fact_list_layout, null);
            if (view == null) {
                view = View.inflate(AmusingFactList.this,R.layout.amusing_fact_list_layout, null);
            }

            TextView mTextView = view.findViewById(R.id.amusingFactListTextView);

            mTextView.setText(vFactNumbersAmusingFactListActivity[position]);

            return view;
        }
    }

    @Override
    protected void onResume() {
        super.onResume();

        SharedPreferences sharedPreferencesAmusingFactListActivity = getSharedPreferences("Background", Context.MODE_PRIVATE);
        int vSelectedColorAmusingFactListActivity = sharedPreferencesAmusingFactListActivity.getInt("integerSelectedColor", 0);
        ConstraintLayout vConstraintLayoutAmusingFactListActivity = findViewById(R.id.constraintLayoutAmusingFactListActivity);
        if (sharedPreferencesAmusingFactListActivity.getInt("integerSelectedColor", Color.WHITE) == vSelectedColorAmusingFactListActivity)
        {vConstraintLayoutAmusingFactListActivity.setBackgroundColor(vSelectedColorAmusingFactListActivity);}
        else
        {vConstraintLayoutAmusingFactListActivity.setBackgroundColor(Color.parseColor("#FDD935"));}

        if(mediaPlayer == null) {

            MusicComponent.BackgroundMusic(AmusingFactList.this, R.raw.bg_music);

        }

        if(mediaPlayer.isPlaying()){

            mediaPlayer.pause();

        }

        mediaPlayer.start();
        MusicComponent.onCompletion(mediaPlayer);

    }

    @Override
    protected void onPause() {
        super.onPause();

        if (!MusicComponent.shouldPlayAmusingFactListActivity){

            mediaPlayer.pause();
            MusicComponent.shouldPlayCategoryActivity = false;
            MusicComponent.shouldPlayAmusingFactListActivity = true;

        }

    }
